// AdminInput.js
import { useState } from 'react';
import axios from 'axios';

function AdminInput() {
  const [text, setText] = useState('');
  const [file, setFile] = useState(null);
  const [results, setResults] = useState(null);
  const [error, setError] = useState('');

  const pageCenter = {
    minHeight: 'calc(100vh - 70px)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  };

  const cardStyle = {
    background: 'rgba(15,23,42,0.94)',
    borderRadius: '18px',
    padding: '24px',
    boxShadow: '0 18px 45px rgba(15,23,42,0.9)',
    border: '1px solid rgba(148,163,184,0.45)',
    maxWidth: '1000px',
    width: '100%'
  };

  const titleStyle = {
    margin: '0 0 4px',
    fontSize: '24px',
    color: '#e5e7eb'
  };

  const subtitleStyle = {
    margin: '0 0 18px',
    color: '#9ca3af',
    fontSize: '14px'
  };

  const formColWrapper = {
    marginTop: '16px',
    display: 'grid',
    gridTemplateColumns: 'minmax(0,1.2fr) minmax(0,1fr)',
    gap: '18px'
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '12px'
  };

  const labelStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '4px',
    fontSize: '13px',
    color: '#e5e7eb'
  };

  const inputStyle = {
    borderRadius: '10px',
    border: '1px solid rgba(148,163,184,0.6)',
    padding: '9px 10px',
    fontSize: '14px',
    background: 'rgba(15,23,42,0.95)',
    color: '#f9fafb'
  };

  const textareaStyle = {
    ...inputStyle,
    minHeight: '140px',
    resize: 'vertical'
  };

  const primaryBtn = {
    borderRadius: '999px',
    padding: '10px 20px',
    fontSize: '14px',
    fontWeight: 500,
    border: 'none',
    cursor: 'pointer',
    background: 'linear-gradient(135deg, #6366f1, #22c55e)',
    color: '#f9fafb',
    width: 'fit-content'
  };

  const secondaryBtn = {
    ...primaryBtn,
    background: 'rgba(15,23,42,0.9)',
    color: '#e5e7eb',
    border: '1px solid rgba(148,163,184,0.7)'
  };

  const sectionTitle = {
    margin: '0 0 6px',
    fontSize: '15px',
    color: '#e5e7eb'
  };

  const alertStyle = {
    padding: '8px 10px',
    borderRadius: '10px',
    fontSize: '13px',
    background: 'rgba(248,113,113,0.1)',
    border: '1px solid rgba(248,113,113,0.6)',
    color: '#fecaca',
    marginTop: '8px'
  };

  const resultsWrapper = {
    marginTop: '22px'
  };

  const tableContainer = {
    marginTop: '10px',
    borderRadius: '12px',
    border: '1px solid rgba(148,163,184,0.6)',
    maxHeight: '260px',
    overflow: 'auto'
  };

  const tableStyle = {
    width: '100%',
    borderCollapse: 'collapse',
    fontSize: '13px'
  };

  const thtd = {
    padding: '8px 10px',
    textAlign: 'left',
    borderBottom: '1px solid rgba(79, 83, 93, 0.8)'
  };

  const theadStyle = {
    background: 'rgba(238, 239, 241, 0.96)'
  };

  const getRowBg = (index) => ({
    background: index % 2 === 0 ? 'rgba(15,23,42,0.94)' : 'rgba(30,41,59,0.92)'
  });

  const handleTextSubmit = async (e) => {
    e.preventDefault();
    if (!text) return;
    try {
      const res = await axios.post(
        'http://localhost:5000/api/analyze-text',
        { text },
        { headers: { 'Content-Type': 'application/json' } }
      );
      setResults([res.data]);
      setError('');
    } catch (err) {
      console.error('Text analyze error:', err.response?.data || err.message);
      setError(
        err.response?.data?.error ||
        err.response?.data?.message ||
        'Server error while analyzing text'
      );
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileSubmit = async (e) => {
    e.preventDefault();
    if (!file) return;
    try {
      const formData = new FormData();
      formData.append('file', file);
      const res = await axios.post(
        'http://localhost:5000/api/upload-csv',
        formData,
        { headers: { 'Content-Type': 'multipart/form-data' } }
      );
      setResults(res.data);
      setError('');
    } catch (err) {
      console.error('CSV upload error:', err.response?.data || err.message);
      setError(
        err.response?.data?.error ||
        'Server error while uploading/processing CSV'
      );
    }
  };

  return (
    <main style={pageCenter}>
      <div style={cardStyle}>
        <h2 style={titleStyle}>Analyze Text / Upload CSV</h2>
        <p style={subtitleStyle}>
          Paste a single text sample or upload a CSV file with multiple rows for batch sentiment analysis.
        </p>

        {error && <div style={alertStyle}>{error}</div>}

        <div style={formColWrapper}>
          <form style={formStyle} onSubmit={handleTextSubmit}>
            <h3 style={sectionTitle}>Single Text Analysis</h3>
            <textarea
              style={textareaStyle}
              placeholder="Enter text to analyze sentiment..."
              value={text}
              onChange={(e) => setText(e.target.value)}
            />
            <button style={primaryBtn} type="submit">
              Analyze Text
            </button>
          </form>

          <form style={formStyle} onSubmit={handleFileSubmit}>
            <h3 style={sectionTitle}>CSV Upload</h3>
            <input
              style={inputStyle}
              type="file"
              accept=".csv"
              onChange={handleFileChange}
            />
            <button style={secondaryBtn} type="submit">
              Upload &amp; Analyze
            </button>
          </form>
        </div>

        {results && (
          <div style={resultsWrapper}>
            <h3 style={sectionTitle}>Results</h3>
            <div style={tableContainer}>
              <table style={tableStyle}>
                <thead style={theadStyle}>
                  <tr>
                    <th style={thtd}>Text</th>
                    <th style={thtd}>Polarity</th>
                    <th style={thtd}>Subjectivity</th>
                    <th style={thtd}>Sentiment</th>
                  </tr>
                </thead>
                <tbody>
                  {results.map((r, index) => (
                    <tr key={index} style={getRowBg(index)}>
                      <td style={thtd}>{r.text}</td>
                      <td style={thtd}>{r.polarity.toFixed(3)}</td>
                      <td style={thtd}>{r.subjectivity.toFixed(3)}</td>
                      <td style={thtd}>{r.sentiment}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </main>
  );
}

export default AdminInput;
