// Navbar.js
import { Link } from 'react-router-dom';

function Navbar() {
  const isLoggedIn = localStorage.getItem("isAdminLoggedIn") === "true";

  return (
    <nav style={{ padding: "10px", backgroundColor: "#4a90e2", color: "white" }}>
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
      >
        <Link
          to="/"
          style={{ margin: "0 10px", color: "white", textDecoration: "none" }}
        >
          Home
        </Link>

        {/* Show Admin Login button only if NOT logged in */}
        {!isLoggedIn && (
          <Link
            to="/admin-login"
            style={{ margin: "0 10px", color: "white", textDecoration: "none" }}
          >
            Admin Login
          </Link>
        )}

        {/* Show protected links ONLY after admin login */}
        {isLoggedIn && (
          <>
            <Link
              to="/user-input"
              style={{ margin: "0 10px", color: "white", textDecoration: "none" }}
            >
              User Input
            </Link>

            <Link
              to="/analysis"
              style={{ margin: "0 10px", color: "white", textDecoration: "none" }}
            >
              Analysis
            </Link>
          </>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
