// Home.js
import React from 'react';

function Home() {
  const wrapperStyle = {
    minHeight: 'calc(100vh - 70px)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  };

  const heroStyle = {
    maxWidth: '1100px',
    width: '100%',
    display: 'grid',
    gridTemplateColumns: 'minmax(0, 1.1fr) minmax(0, 1fr)',
    gap: '40px',
    alignItems: 'center'
  };

  const titleStyle = {
    fontSize: '40px',
    lineHeight: 1.1,
    margin: '0 0 16px',
    color: '#e5e7eb'
  };

  const titleAccentStyle = {
    background: 'linear-gradient(135deg, #6366f1, #06b6d4, #22c55e)',
    WebkitBackgroundClip: 'text',
    color: 'transparent'
  };

  const subtitleStyle = {
    margin: '0 0 24px',
    color: '#cbd5f5',
    fontSize: '15px',
    maxWidth: '460px'
  };

  const ctaGroupStyle = {
    display: 'flex',
    gap: '12px',
    flexWrap: 'wrap'
  };

  const primaryBtn = {
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: '999px',
    padding: '10px 22px',
    fontSize: '14px',
    fontWeight: 500,
    border: 'none',
    cursor: 'pointer',
    textDecoration: 'none',
    background: 'linear-gradient(135deg, #6366f1, #22c55e)',
    color: '#f9fafb',
    boxShadow: '0 18px 45px rgba(79,70,229,0.45)'
  };

  const secondaryBtn = {
    ...primaryBtn,
    background: 'rgba(15,23,42,0.9)',
    color: '#e5e7eb',
    border: '1px solid rgba(148,163,184,0.6)',
    boxShadow: 'none'
  };

  const visualWrapper = {
    display: 'flex',
    justifyContent: 'center'
  };

  const heroCard = {
    width: '100%',
    maxWidth: '420px',
    padding: '20px 18px 18px',
    borderRadius: '18px',
    background:
      'linear-gradient(145deg, rgba(148,163,184,0.18), rgba(37,99,235,0.38)), radial-gradient(circle at top left, rgba(125,211,252,0.35), transparent)',
    boxShadow: '0 18px 45px rgba(15,23,42,0.9)',
    border: '1px solid rgba(148,163,184,0.45)'
  };

  const chipRow = {
    display: 'flex',
    gap: '8px',
    marginBottom: '16px',
    flexWrap: 'wrap'
  };

  const chipBase = {
    fontSize: '11px',
    padding: '4px 10px',
    borderRadius: '999px',
    background: 'rgba(15,23,42,0.85)',
    color: '#e5e7eb',
    border: '1px solid rgba(148,163,184,0.7)'
  };

  const chipPositive = {
    ...chipBase,
    borderColor: '#22c55e',
    color: '#bbf7d0'
  };

  const chipNeutral = {
    ...chipBase,
    borderColor: '#38bdf8',
    color: '#e0f2fe'
  };

  const chipNegative = {
    ...chipBase,
    borderColor: '#f97373',
    color: '#fee2e2'
  };

  const graphPlaceholder = {
    position: 'relative',
    height: '160px',
    borderRadius: '14px',
    background: 'radial-gradient(circle at top, #020617 0, #020617 35%, #050816 100%)',
    overflow: 'hidden'
  };

  const graphLineBase = {
    position: 'absolute',
    width: '140%',
    height: '3px',
    borderRadius: '999px',
    filter: 'drop-shadow(0 8px 16px rgba(0,0,0,0.9))'
  };

  const line1 = {
    ...graphLineBase,
    top: '40%',
    left: '-10%',
    background: 'linear-gradient(90deg, #22c55e, #4ade80, #a3e635)'
  };

  const line2 = {
    ...graphLineBase,
    top: '55%',
    left: '-20%',
    background: 'linear-gradient(90deg, #38bdf8, #6366f1)'
  };

  const line3 = {
    ...graphLineBase,
    top: '70%',
    left: '-8%',
    background: 'linear-gradient(90deg, #f97373, #fb7185, #facc15)'
  };

  const caption = {
    marginTop: '14px',
    fontSize: '12px',
    color: '#e5e7eb',
    opacity: 0.9
  };

  return (
    <main style={wrapperStyle}>
      <section style={heroStyle}>
        <div>
          <h1 style={titleStyle}>
            Powerful <span style={titleAccentStyle}>Sentiment</span> Analysis Dashboard
          </h1>
          <p style={subtitleStyle}>
            Analyze text sentiments as Positive, Negative, or Neutral with rich polarity and
            subjectivity scores. Upload CSV data or test single messages live.
          </p>

          <div style={ctaGroupStyle}>
            <a href="/admin-login" style={primaryBtn}>
              Get Started
            </a>
            <a href="/analysis" style={secondaryBtn}>
              View Insights
            </a>
          </div>
        </div>

        <div style={visualWrapper}>
          <div style={heroCard}>
            <div style={chipRow}>
              <span style={chipPositive}>Positive</span>
              <span style={chipNeutral}>Neutral</span>
              <span style={chipNegative}>Negative</span>
            </div>
            <div style={graphPlaceholder}>
              <div style={line1} />
              <div style={line2} />
              <div style={line3} />
            </div>
            <p style={caption}>
              Beautiful visual summaries to explore trends in your sentiment data.
            </p>
          </div>
        </div>
      </section>
    </main>
  );
}

export default Home;
