// Home.js
import React from 'react';

function Home() {
  const wrapperStyle = {
    minHeight: 'calc(100vh - 70px)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '32px 16px',
  };

  const heroStyle = {
    maxWidth: '1000px',
    width: '100%',
    textAlign: 'center',
  };

  const titleStyle = {
    fontSize: '36px',
    lineHeight: 1.2,
    margin: '0 0 16px 0',
    color: '#f8fafc',
    fontWeight: 800,
  };

  const titleAccentStyle = {
    background: 'linear-gradient(135deg, #3b82f6, #10b981)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    backgroundClip: 'text',
  };

  const subtitleStyle = {
    margin: '0 0 28px',
    color: '#cbd5e1',
    fontSize: '15px',
    lineHeight: 1.5,
    maxWidth: '520px',
    marginLeft: 'auto',
    marginRight: 'auto',
  };

  const ctaGroupStyle = {
    display: 'flex',
    gap: '12px',
    flexWrap: 'wrap',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: '28px',
  };

  const primaryBtn = {
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '10px 22px',
    fontSize: '14px',
    fontWeight: 600,
    borderRadius: '999px',
    border: 'none',
    cursor: 'pointer',
    textDecoration: 'none',
    background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
    color: 'white',
    boxShadow: '0 6px 18px rgba(59, 130, 246, 0.35)',
  };

  const secondaryBtn = {
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '10px 22px',
    fontSize: '14px',
    fontWeight: 600,
    borderRadius: '999px',
    border: '1px solid #475569',
    cursor: 'pointer',
    textDecoration: 'none',
    background: 'transparent',
    color: '#e2e8f0',
  };

  const featuresRow = {
    display: 'flex',
    gap: '16px',
    justifyContent: 'center',
    alignItems: 'stretch',
    flexWrap: 'wrap', // keeps them in one row on desktop, wraps only on very small screens
    marginTop: '8px',
  };

  const featureCard = {
    flex: '0 1 260px',
    maxWidth: '300px',
    padding: '20px 18px',
    background: 'rgba(15, 23, 42, 0.8)',
    borderRadius: '14px',
    border: '1px solid rgba(148, 163, 184, 0.3)',
    textAlign: 'left',
  };

  const featureHeaderRow = {
    display: 'flex',
    alignItems: 'center',
    gap: '12px',
    marginBottom: '10px',
  };

  const iconBox = {
    width: '40px',
    height: '40px',
    borderRadius: '12px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '20px',
    color: 'white',
  };

  const featureTitle = {
    margin: 0,
    fontSize: '16px',
    color: '#f9fafb',
    fontWeight: 700,
  };

  const featureText = {
    margin: 0,
    color: '#cbd5e1',
    fontSize: '13px',
    lineHeight: 1.5,
  };

  return (
    <main style={wrapperStyle}>
      <section style={heroStyle}>
        <h1 style={titleStyle}>
          Simple <span style={titleAccentStyle}>Sentiment</span> Analysis
        </h1>
        <p style={subtitleStyle}>
          Quickly classify text as positive, negative, or neutral and view clear sentiment scores.
        </p>

        <div style={ctaGroupStyle}>
          <a href="/admin-login" style={primaryBtn}>
            Get Started
          </a>
          <a href="/analysis" style={secondaryBtn}>
            Try Demo
          </a>
        </div>

        <div style={featuresRow}>
          <div style={featureCard}>
            <div style={featureHeaderRow}>
              <div
                style={{
                  ...iconBox,
                  background: 'linear-gradient(135deg, #22c55e, #4ade80)',
                }}
              >
                ðŸ“„
              </div>
              <h3 style={featureTitle}>Text Analysis</h3>
            </div>
            <p style={featureText}>
              Upload CSV files or check single messages in real-time.
            </p>
          </div>

          <div style={featureCard}>
            <div style={featureHeaderRow}>
              <div
                style={{
                  ...iconBox,
                  background: 'linear-gradient(135deg, #3b82f6, #60a5fa)',
                }}
              >
                ðŸ“Š
              </div>
              <h3 style={featureTitle}>Visual Reports</h3>
            </div>
            <p style={featureText}>
              See sentiment distribution with simple, clear charts.
            </p>
          </div>

          <div style={featureCard}>
            <div style={featureHeaderRow}>
              <div
                style={{
                  ...iconBox,
                  background: 'linear-gradient(135deg, #f97316, #fbbf24)',
                }}
              >
                âš¡
              </div>
              <h3 style={featureTitle}>Real-time Results</h3>
            </div>
            <p style={featureText}>
              Instant processing with polarity and subjectivity scores.
            </p>
          </div>
        </div>
      </section>
    </main>
  );
}

export default Home;
