import { Chart, LinearScale, PointElement, Tooltip, Legend, CategoryScale, Title, LineElement } from 'chart.js';

Chart.register(LinearScale, PointElement, Tooltip, Legend, CategoryScale, Title, LineElement);

import { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2'; // Use Line for a line chart


function AnalysisPage() {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const res = await axios.get('http://localhost:5000/api/analysis-data');
      setData(res.data);
    }
    fetchData();
  }, []);

  const chartData = {
    datasets: [{
      label: 'Polarity vs Subjectivity',
      data: data.map((d, i) => ({ x: d.polarity, y: d.subjectivity })),
      backgroundColor: 'rgba(75, 192, 192, 0.6)'
    }]
  };

  const options = {
    scales: {
      x: { title: { display: true, text: 'Polarity (-1 to 1)' }, min: -1, max: 1 },
      y: { title: { display: true, text: 'Subjectivity (0 to 1)' }, min: 0, max: 1 }
    }
  };

  return (
    <div style={{maxWidth: "700px", margin: "auto", marginTop: "30px"}}>
      <h2>Sentiment Analysis Graph</h2>
      <Scatter data={chartData} options={options} />
    </div>
  );
}

export default AnalysisPage;
