// Home.js
import React from "react";

function Home() {
  const wrapperStyle = {
    minHeight: "calc(100vh - 70px)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "32px 18px",
  };

  const heroStyle = {
    maxWidth: "1100px",
    width: "100%",
    display: "grid",
    gridTemplateColumns: "minmax(0, 1.1fr) minmax(0, 1fr)",
    gap: "40px",
    alignItems: "center",
  };

  const titleStyle = {
    fontSize: "42px",
    lineHeight: 1.1,
    margin: "0 0 18px",
    color: "#e5e7eb",
  };

  const titleAccentStyle = {
    background: "linear-gradient(135deg, #6366f1, #06b6d4, #22c55e)",
    WebkitBackgroundClip: "text",
    color: "transparent",
  };

  const subtitleStyle = {
    margin: "0 0 26px",
    color: "#9ca3af",
    fontSize: "15px",
    maxWidth: "480px",
  };

  const ctaGroupStyle = {
    display: "flex",
    gap: "12px",
    flexWrap: "wrap",
  };

  const primaryBtn = {
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: "999px",
    padding: "10px 22px",
    fontSize: "14px",
    fontWeight: 500,
    border: "none",
    cursor: "pointer",
    textDecoration: "none",
    background: "linear-gradient(135deg, #6366f1, #22c55e)",
    color: "#f9fafb",
    boxShadow: "0 18px 45px rgba(79,70,229,0.45)",
    transition: "transform 0.18s ease, box-shadow 0.18s ease, opacity 0.18s ease",
  };

  const secondaryBtn = {
    ...primaryBtn,
    background: "rgba(15,23,42,0.9)",
    color: "#e5e7eb",
    border: "1px solid rgba(148,163,184,0.6)",
    boxShadow: "none",
  };

  const visualWrapper = {
    display: "flex",
    justifyContent: "center",
  };

  const heroCard = {
    width: "100%",
    maxWidth: "420px",
    padding: "22px 20px 20px",
    borderRadius: "20px",
    background:
      "linear-gradient(145deg, rgba(148,163,184,0.18), rgba(37,99,235,0.35)), radial-gradient(circle at top left, rgba(56,189,248,0.28), transparent)",
    boxShadow: "0 22px 55px rgba(15,23,42,0.95)",
    border: "1px solid rgba(148,163,184,0.5)",
    backdropFilter: "blur(18px)",
  };

  const chipRow = {
    display: "flex",
    gap: "8px",
    marginBottom: "16px",
    flexWrap: "wrap",
  };

  const chipBase = {
    fontSize: "11px",
    padding: "4px 10px",
    borderRadius: "999px",
    background: "rgba(15,23,42,0.9)",
    color: "#e5e7eb",
    border: "1px solid rgba(148,163,184,0.7)",
  };

  const chipPositive = {
    ...chipBase,
    borderColor: "#22c55e",
    color: "#bbf7d0",
  };

  const chipNeutral = {
    ...chipBase,
    borderColor: "#38bdf8",
    color: "#e0f2fe",
  };

  const chipNegative = {
    ...chipBase,
    borderColor: "#f97373",
    color: "#fee2e2",
  };

  // simple image box instead of lines
  const imageBox = {
    marginTop: "8px",
    borderRadius: "16px",
    overflow: "hidden",
    border: "1px solid rgba(15,23,42,0.9)",
  };

  const imageStyle = {
    display: "block",
    width: "100%",
    height: "190px",
    objectFit: "cover",
  };

  const caption = {
    marginTop: "14px",
    fontSize: "12px",
    color: "#e5e7eb",
    opacity: 0.9,
  };

  const isSmallScreen = window.innerWidth < 900;
  const heroResponsive = isSmallScreen
    ? { ...heroStyle, gridTemplateColumns: "minmax(0,1fr)", rowGap: "32px" }
    : heroStyle;

  return (
    <main style={wrapperStyle}>
      <section style={heroResponsive}>
        <div>
          <h1 style={titleStyle}>
            Powerful <span style={titleAccentStyle}>Sentiment</span> Analysis
            Dashboard
          </h1>
          <p style={subtitleStyle}>
            Analyze text sentiments as Positive, Negative, or Neutral with rich
            polarity and subjectivity scores. Upload CSV data or test single
            messages live.
          </p>

          <div style={ctaGroupStyle}>
            <a href="/admin-login" style={primaryBtn}>
              Get Started
            </a>
            <a href="/analysis" style={secondaryBtn}>
              View Insights
            </a>
          </div>
        </div>

        <div style={visualWrapper}>
          <div style={heroCard}>
            <div style={chipRow}>
              <span style={chipPositive}>Positive</span>
              <span style={chipNeutral}>Neutral</span>
              <span style={chipNegative}>Negative</span>
            </div>

            {/* simple image instead of custom lines */}
            <div style={imageBox}>
              {/* replace /sentiment-preview.png with your actual image path in public/ */}
              <img
                src="/sentiment-preview.png"
                alt="Sentiment analysis preview"
                style={imageStyle}
              />
            </div>

            <p style={caption}>
              Beautiful visual summaries to explore trends in your sentiment
              data.
            </p>
          </div>
        </div>
      </section>
    </main>
  );
}

export default Home;
