// AnalysisPage.js
import React, { useState } from "react";

function AnalysisPage() {
  const [text, setText] = useState("");
  const [results, setResults] = useState(null);

  const cardStyle = {
    maxWidth: "1200px",
    margin: "40px auto",
    padding: "24px 26px 28px",
    borderRadius: "22px",
    background: "rgba(8,15,30,0.98)",
    border: "1px solid rgba(148,163,184,0.7)",
    boxShadow: "0 24px 60px rgba(15,23,42,0.95)",
    color: "#e5e7eb",
  };

  const headerStyle = {
    fontSize: "26px",
    marginBottom: "6px",
  };

  const subTextStyle = {
    fontSize: "14px",
    color: "#9ca3af",
    marginBottom: "22px",
  };

  const layoutStyle = {
    display: "grid",
    gridTemplateColumns: "minmax(0, 1.1fr) minmax(0, 0.9fr)",
    gap: "24px",
    alignItems: "flex-start",
    marginBottom: "26px",
  };

  const labelStyle = {
    fontSize: "14px",
    fontWeight: 500,
    marginBottom: "8px",
  };

  const textareaStyle = {
    width: "100%",
    minHeight: "170px",
    borderRadius: "14px",
    border: "1px solid rgba(75,85,99,0.9)",
    padding: "12px 14px",
    backgroundColor: "#020617",
    color: "#f9fafb",
    fontSize: "14px",
    resize: "vertical",
  };

  const fileBoxStyle = {
    padding: "12px 14px",
    borderRadius: "14px",
    border: "1px dashed rgba(148,163,184,0.7)",
    backgroundColor: "rgba(15,23,42,0.7)",
    color: "#e5e7eb",
    fontSize: "13px",
  };

  const buttonPrimary = {
    marginTop: "12px",
    borderRadius: "999px",
    padding: "9px 22px",
    border: "none",
    cursor: "pointer",
    background: "linear-gradient(135deg,#6366f1,#22c55e)",
    color: "#f9fafb",
    fontSize: "14px",
    fontWeight: 500,
  };

  const buttonSecondary = {
    ...buttonPrimary,
    background: "rgba(15,23,42,0.9)",
    border: "1px solid rgba(148,163,184,0.7)",
  };

  // RESULTS TABLE STYLES (your teal theme)
  const resultsSection = {
    marginTop: "18px",
  };

  const resultsTitle = {
    fontSize: "16px",
    fontWeight: 600,
    marginBottom: "10px",
  };

  const tableWrapper = {
    borderRadius: "16px",
    overflow: "hidden",
    border: "1px solid #0d9488", // teal border
    backgroundColor: "#f0fdfa", // very light teal background
  };

  const tableStyle = {
    width: "100%",
    borderCollapse: "collapse",
    fontSize: "13px",
  };

  const thStyle = {
    textAlign: "left",
    padding: "10px 14px",
    background: "linear-gradient(135deg, #0f766e, #14b8a6)", // teal gradient
    color: "#ffffff",
    borderBottom: "1px solid #0d9488",
  };

  const tdStyle = {
    padding: "9px 14px",
    color: "#f7f8fbff", // dark text
    borderTop: "1px solid #0d9488",
  };

  const tdMuted = {
    ...tdStyle,
    color: "#334155", // slightly muted dark gray
  };

  const rowAlt = {
    backgroundColor: "#ccfbf1", // alternate row light teal
  };

  const handleAnalyze = () => {
    // example data so row is always visible
    setResults({
      text: text || "Sample text...",
      polarity: 0.625,
      subjectivity: 0.45,
      sentiment: "Positive",
    });
  };

  return (
    <main style={{ padding: "32px 18px" }}>
      <div style={cardStyle}>
        <h2 style={headerStyle}>Analyze Text / Upload CSV</h2>
        <p style={subTextStyle}>
          Paste a single text sample or upload a CSV file with multiple rows for
          batch sentiment analysis.
        </p>

        <div style={layoutStyle}>
          <div>
            <div style={labelStyle}>Single Text Analysis</div>
            <textarea
              style={textareaStyle}
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Type or paste text to analyze..."
            />
            <button style={buttonPrimary} onClick={handleAnalyze}>
              Analyze Text
            </button>
          </div>

          <div>
            <div style={labelStyle}>CSV Upload</div>
            <div style={fileBoxStyle}>
              <input type="file" accept=".csv" />
              <button style={buttonSecondary}>Upload &amp; Analyze</button>
            </div>
          </div>
        </div>

        <section style={resultsSection}>
          <div style={resultsTitle}>Results</div>
          {results && (
            <div style={tableWrapper}>
              <table style={tableStyle}>
                <thead>
                  <tr>
                    <th style={thStyle}>Text</th>
                    <th style={thStyle}>Polarity</th>
                    <th style={thStyle}>Subjectivity</th>
                    <th style={thStyle}>Sentiment</th>
                  </tr>
                </thead>
                <tbody>
                  <tr style={rowAlt}>
                    <td style={tdMuted}>{results.text}</td>
                    <td style={tdStyle}>{results.polarity.toFixed(3)}</td>
                    <td style={tdStyle}>{results.subjectivity.toFixed(3)}</td>
                    <td style={tdStyle}>{results.sentiment}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          )}
        </section>
      </div>
    </main>
  );
}

export default AnalysisPage;
