// Home.js
import React from 'react';
import heroImage from './image.jpg'; // adjust path if needed

function Home() {
  const wrapperStyle = {
    minHeight: 'calc(100vh - 70px)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '40px 20px'
  };

  const heroStyle = {
    maxWidth: '1000px',
    width: '100%',
    display: 'grid',
    gridTemplateColumns: '1.1fr 0.9fr',
    gap: '40px',
    alignItems: 'center'
  };

  const titleStyle = {
    fontSize: '44px',
    lineHeight: 1.2,
    margin: '0 0 20px 0',
    color: '#f8fafc',
    fontWeight: '800'
  };

  const titleAccentStyle = {
    background: 'linear-gradient(135deg, #3b82f6, #10b981)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    backgroundClip: 'text'
  };

  const subtitleStyle = {
    margin: '0 0 28px',
    color: '#cbd5e1',
    fontSize: '16px',
    lineHeight: 1.6,
    maxWidth: '480px'
  };

  const ctaGroupStyle = {
    display: 'flex',
    gap: '16px',
    flexWrap: 'wrap',
    alignItems: 'center'
  };

  const primaryBtn = {
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '12px 26px',
    fontSize: '15px',
    fontWeight: '600',
    borderRadius: '999px',
    border: 'none',
    cursor: 'pointer',
    textDecoration: 'none',
    background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
    color: 'white',
    boxShadow: '0 8px 22px rgba(59, 130, 246, 0.35)'
  };

  const secondaryBtn = {
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '12px 26px',
    fontSize: '15px',
    fontWeight: '600',
    borderRadius: '999px',
    border: '1px solid #475569',
    cursor: 'pointer',
    textDecoration: 'none',
    background: 'transparent',
    color: '#e2e8f0'
  };

  const imageWrapper = {
    display: 'flex',
    justifyContent: 'center'
  };

  const imageStyle = {
    width: '100%',
    maxWidth: '420px',
    borderRadius: '18px',
    border: '1px solid rgba(148,163,184,0.35)',
    boxShadow: '0 18px 40px rgba(15,23,42,0.9)',
    objectFit: 'cover'
  };

  return (
    <main style={wrapperStyle}>
      <section style={heroStyle}>
        <div>
          <h1 style={titleStyle}>
            Simple <span style={titleAccentStyle}>Sentiment</span> Dashboard
          </h1>
          <p style={subtitleStyle}>
            Analyze text as positive, negative, or neutral in seconds. Upload CSV files or test
            single messages live with clear, easy-to-read results.
          </p>

          <div style={ctaGroupStyle}>
            <a href="/admin-login" style={primaryBtn}>
              Get Started
            </a>
            <a href="/analysis" style={secondaryBtn}>
              Try Demo
            </a>
          </div>
        </div>

        <div style={imageWrapper}>
          <img src={heroImage} alt="Sentiment analysis preview" style={imageStyle} />
        </div>
      </section>
    </main>
  );
}

export default Home;
